import { Divider, Grid } from "@mui/material";
import SideBar from "./SideBar/SideBar";
import NewTwist from "./Twist/NewTist";
import ListTwist from "./Twist/ListTwist";
import { useState } from "react";
const Home = ({ disconnect }) => {
  const [twists, setTwists] = useState([
    {
      id: 1,
      user: {
        name: "Rocco",
        surname: "SIFFREDDI",
      },
      message:
        "Aute laborum ipsum ut proident nostrud cupidatat nostrud consectetur fugiat. Id pariatur proident consectetur ipsum commodo magna excepteur non. Magna adipisicing et dolore do cupidatat consectetur cupidatat anim aute voluptate enim. Lorem exercitation ut labore Lorem cupidatat ut eiusmod anim. Velit labore elit nisi labore ullamco velit sunt consequat ipsum est cillum dolore. Id ea ullamco mollit excepteur enim amet minim aliquip ut occaecat deserunt.",
      likesCount: 3,
    },
    {
      id: 2,
      user: {
        name: "Diva",
        surname: "REBECCA",
      },
      message:
        "Aute laborum ipsum ut proident nostrud cupidatat nostrud consectetur fugiat. Id pariatur proident consectetur ipsum commodo magna excepteur non. Magna adipisicing et dolore do cupidatat consectetur cupidatat anim aute voluptate enim. Lorem exercitation ut labore Lorem cupidatat ut eiusmod anim. Velit labore elit nisi labore ullamco velit sunt consequat ipsum est cillum dolore. Id ea ullamco mollit excepteur enim amet minim aliquip ut occaecat deserunt.",
      likesCount: 5,
    },
    {
      id: 3,
      user: {
        name: "Johny",
        surname: "SINS",
      },
      message:
        "Aute laborum ipsum ut proident nostrud cupidatat nostrud consectetur fugiat. Id pariatur proident consectetur ipsum commodo magna excepteur non. Magna adipisicing et dolore do cupidatat consectetur cupidatat anim aute voluptate enim. Lorem exercitation ut labore Lorem cupidatat ut eiusmod anim. Velit labore elit nisi labore ullamco velit sunt consequat ipsum est cillum dolore. Id ea ullamco mollit excepteur enim amet minim aliquip ut occaecat deserunt.",
      likesCount: 69,
    },
  ]);
  const addTwist = (content) => {
    let newItem = {
      message: content,
      user: {
        name: "Hadi",
        surname: "CHITER",
      },
      likesCount: 0,
    };
    setTwists([newItem].concat(twists));
  };
  return (
    <Grid container direction="row" justifyContent="space-around" mt={3}>
      <Grid item xs={1}>
        <SideBar disconnect={disconnect}></SideBar>
      </Grid>
      <Grid container item direction="row" xs={9}>
        <NewTwist addTwist={addTwist}></NewTwist>
        <ListTwist twists={twists}></ListTwist>
      </Grid>
    </Grid>
  );
};

export default Home;
