  const [showForgot, setForgotView] = useState(false);
  const changeLoginState = () => {
    setLoginView(!showLogin);
  };
  const changeRegisterState = () => {
    setRegisterView(!showRegister);
  };
  const changeForgotPasswordState = () => {
    console.log("azul");
    setForgotView(!showForgot);
  };
  return (
    <Welcome
      showLogin={showLogin}
      handleLogin={changeLoginState}
      showRegister={showRegister}
      handleRegister={changeRegisterState}
      showForgot={showForgot}
      handleForgot={changeForgotPasswordState}
    />
  );
}
export default App;
