import "../css/login.css";
import Button from "./Button";
import logo from "./../img/logo.png";
import banner from "./../img/banner.png";

import { useState } from "react";
import Forgotten from "./forgottenps";
import Home from "./Home/Home";

const Login = ({ login, register }) => {
  const [forgotten, setForgotten] = useState(false);
  const [connected, setConnected] = useState(false);
  const didForgot = () => {
    setForgotten(!forgotten);
  };

  const backRegister = () => {
    register();
    login();
  };

  const backToWeclome = () => {
    login();
  };

  const connect = () => {
    setConnected(!connected);
  };
  return connected ? (
    <Home disconnect={connect} />
  ) : !forgotten ? (
    <>
      <p className="textClick" id="backWelcome" onClick={backToWeclome}>
        {" "}
        Retour à la page d'accueil
      </p>
      <div className="loginContainer">
        <div className="bannerContainer">
          <img src={banner} alt="banner"></img>
        </div>
        <div className="formContainer">
          <img src={logo} alt="logo" className="logo"></img>
          <h1 className="banner">Connectez-Vous</h1>
          <div className="form">
            <input id="username" type="email" placeholder="Email"></input>
            <input
              id="password"
              type="password"
              placeholder="Mot de Passe"
            ></input>
            <p className="textClick" onClick={didForgot}>
              Mot de passe oublié?
            </p>
            <Button
              backgroundColor="#1D9BF0"
              className="btn signin"
              onClick={connect}
            >
              Connexion
            </Button>
            <p className="textClick" id="backRegister" onClick={backRegister}>
              Vous n'avez pas de compte ? Inscrivez-vous !
            </p>
          </div>
        </div>
      </div>
    </>
  ) : (
    <Forgotten forgotten={didForgot}></Forgotten>
  );
};

export default Login;
