import { Grid, Avatar, Input, Button } from "@mui/material";
import { deepOrange } from "@mui/material/colors";

const AddTwist = () => {
  return (
    <Grid
      container
      direction="column"
      xs={8}
      padding={3}
      sx={{
        border: "1px solid",
        borderColor: "divider",
        borderBottom: "7px solid",
        borderBottomColor: "divider",
      }}
    >
      <Grid
        container
        item
        columnSpacing={2}
        direction="row"
        justifyContent="flex-start"
      >
        <Grid item>
          <Avatar alt="userAvatar" sx={{ bgcolor: deepOrange[500] }}>
            HC
          </Avatar>
        </Grid>
        <Grid item xs={10}>
          <Input
            type="text"
            placeholder="Azul"
            disableUnderline
            multiline
            maxRows={4}
            fullWidth
          ></Input>
        </Grid>
      </Grid>
      <Grid item alignSelf="end">
        <Button
          variant="contained"
          sx={{ bgcolor: "#1D9BF0", borderRadius: "1000px", fontWeight: 900 }}
          size="medium"
        >
          Twister
        </Button>
      </Grid>
    </Grid>
  );
};

export default AddTwist;
