import {
  ChatBubbleOutline,
  Favorite,
  FavoriteBorder,
} from "@mui/icons-material";
import { Avatar, IconButton, Typography } from "@mui/material";
import { deepPurple } from "@mui/material/colors";
import Grid from "@mui/material/Grid";
import { useState } from "react";
const Twist = () => {
  const [isLiked, setIsLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(0);
  const toggleLike = () => {
    !isLiked ? setLikesCount(likesCount + 1) : setLikesCount(likesCount - 1);
    setIsLiked(!isLiked);
  };
  return (
    <Grid
      container
      columnSpacing={3}
      xs={6}
      sx={{ border: "1px solid", borderColor: "divider", py: 2 }}
      px={2.5}
    >
      <Grid item xs={1}>
        <Avatar alt="userAvatar" sx={{ bgcolor: deepPurple[500] }}>
          MZ
        </Avatar>
      </Grid>
      <Grid item container xs={11} rowSpacing={1}>
        <Grid item>
          <Typography variant="subtitle2">Mehdi ZENINE</Typography>
        </Grid>
        <Grid item>
          <Typography variant="body2">
            Ullamco irure sint laboris sint minim ullamco officia laborum Lorem
            laborum ullamco aliquip. Proident consectetur Lorem ipsum labore
            deserunt esse. Cupidatat exercitation consequat laboris nisi aliquip
            proident velit pariatur cillum
          </Typography>
        </Grid>
        <Grid item>
          <IconButton disableRipple>
            <ChatBubbleOutline />
          </IconButton>
        </Grid>
        <Grid item>
          <IconButton
            disableRipple
            disableTouchRipple
            disableFocusRipple
            onClick={toggleLike}
          >
            {isLiked ? <Favorite color="error" /> : <FavoriteBorder />}
            <Typography ml={1}>{likesCount}</Typography>
          </IconButton>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Twist;
