import { Contacts, Person } from "@mui/icons-material";
import { Avatar, Button, Grid, Typography } from "@mui/material";
import { deepOrange } from "@mui/material/colors";
import { useState } from "react";
import ListTwist from "../Twist/ListTwist";
import NavBar from "./NavBar";

const Profile = () => {
  const [tabValue, setTabValue] = useState(0);
  const tabs = [<ListTwist />, <ListTwist />];
  const handleTabChange = (value) => {
    setTabValue(value);
  };
  return (
    <Grid
      container
      alignItems="center"
      direction="column"
      sx={{
        borderLeft: "1px solid",
        borderColor: "divider",
        width: "100%",
        height: "100%",
      }}
      pt={10}
    >
      <Avatar sx={{ bgcolor: deepOrange[300], width: 100, height: 100 }}>
        <Person sx={{ fontSize: 70 }}></Person>
      </Avatar>
      <Typography variant="h5" mt={2}>
        Elhadi CHITER
      </Typography>
      <Button variant="contained" sx={{ width: "30%" }}>
        Ajouter
      </Button>
      <Grid alignSelf="flex-start" item container pl={4}>
        <Typography variant="h6">999 Amis</Typography>
      </Grid>
      <Grid item container>
        <NavBar handleChange={handleTabChange}></NavBar>
        {tabs[tabValue]}
      </Grid>
    </Grid>
  );
};

export default Profile;
