import { Grid, Avatar, Input, Button } from "@mui/material";
import { deepOrange } from "@mui/material/colors";
import { useState } from "react";
const NewTwist = ({ addTwist }) => {
  const [content, setContent] = useState("");
  return (
    <Grid
      container
      direction="column"
      justifyContent="flex-start"
      sx={{
        border: "1px solid",
        borderColor: "divider",
        borderBottom: "6px solid",
        borderBottomColor: "divider",
        height: "15%",
      }}
    >
      <Grid
        container
        item
        columnSpacing={2}
        direction="row"
        justifyContent="flex-start"
      >
        <Grid item>
          <Avatar alt="userAvatar" sx={{ bgcolor: deepOrange[500] }}>
            HC
          </Avatar>
        </Grid>
        <Grid item xs={10} sx={{ border: "1px solid black" }}>
          <Input
            type="text"
            placeholder="Azul"
            disableUnderline
            multiline
            maxRows={4}
            fullWidth
            value={content}
            onChange={(e) => {
              setContent(e.target.value);
            }}
          ></Input>
        </Grid>
      </Grid>
      <Button
        variant="contained"
        size="medium"
        onClick={(e) => {
          addTwist(content);
          setContent("");
        }}
      >
        Twister
      </Button>
    </Grid>
  );
};

export default NewTwist;
