import { Home, Logout, PersonOutline } from "@mui/icons-material";
import { Tabs, Tab, Grid, Button } from "@mui/material";
import { useState } from "react";
const SideBar = ({ disconnect }) => {
  const [tabValue, setTabValue] = useState(0);
  const handleTabChange = (event, value) => {
    setTabValue(value);
  };
  return (
    <Grid direction="column" container alignItems="center">
      <Grid item>
        <Tabs
          orientation="vertical"
          onChange={handleTabChange}
          value={tabValue}
          TabIndicatorProps={{
            style: { display: "none" },
          }}
        >
          <Tab
            icon={<Home />}
            label="Accueil"
            iconPosition="start"
            disableRipple
            disableTouchRipple
            disableFocusRipple
          />
          <Tab
            icon={<PersonOutline />}
            label="Profil"
            iconPosition="start"
            disableRipple
            disableTouchRipple
            disableFocusRipple
          />
        </Tabs>
      </Grid>
      <Grid item>
        <Button
          variant="contained"
          onClick={disconnect}
          sx={{ bgcolor: "#1D9BF0", borderRadius: "1000px", fontWeight: 900 }}
        >
          Deconnexion
        </Button>
      </Grid>
    </Grid>
  );
};

export default SideBar;
