import "../css/welcome.css";
// import Button from "./Button";
import logo from "./../img/logo.png";
import banner from "./../img/banner.png";
import { Button } from "@mui/material";
import Login from "./login";
import Signup from "./signup";
import Forgotten from "./forgottenps";
const Welcome = ({}) => {
  const [showLogin, setLoginView] = useState(false);
  const [showRegister, setRegisterView] = useState(false);
  const [showForgot, setForgotView] = useState(false);
  const changeLoginState = () => {
    setLoginView(!showLogin);
  };
  const changeRegisterState = () => {
    setRegisterView(!showRegister);
  };
  const changeForgotPasswordState = () => {
    setForgotView(!showForgot);
  };
  return showLogin ? (
    <Login
      changeForgotView={handleForgot}
      changeRegisterView={handleRegister}
    />
  ) : showRegister ? (
    <Signup />
  ) : showForgot ? (
    <Forgotten />
  ) : (
    <>
      <div className="welcomeContainer">
        <div className="bannerContainer">
          <img src={banner} alt="banner"></img>
        </div>
        <div className="welcomeForm">
          <h1> Welcome to Twister </h1>
          <img src={logo} alt="logo" className="logo"></img>
          <Button
            variant="contained"
            size="large"
            sx={{ width: "60%" }}
            onClick={handleLogin}
          >
            Connexion
          </Button>
          <Button
            variant="contained"
            size="large"
            sx={{ width: "60%" }}
            onClick={handleRegister}
          >
            Inscription
          </Button>
        </div>
      </div>
    </>
  );
};

export default Welcome;
