import "../css/welcome.css";
import Button from "./Button";
import logo from "./../img/logo.png";
import banner from "./../img/banner.png";

import Login from "./login";
import Signup from "./signup";

import { useState } from "react";

const Welcome = () => {
  const [toRegister, setRegister] = useState(false);

  const getRegistered = () => {
    setRegister(!toRegister);
  };

  const getConnected = () => {
    setConnect(!toConnect);
  };

  return toConnect ? (
    <Login login={getConnected} register={getRegistered}></Login>
  ) : toRegister ? (
    <Signup login={getConnected} register={getRegistered} />
  ) : (
    <div className="welcomeContainer">
      <div className="bannerContainer">
        <img src={banner} alt="banner"></img>
      </div>
      <div className="welcomeForm">
        <h1> Welcome to Twister </h1>
        <img src={logo} alt="logo" className="logo"></img>
        <Button
          backgroundColor="#1D9BF0"
          className="btn signin"
          onClick={getConnected}
        >
          Connexion
        </Button>
        <Button
          outline="#1D9BF0"
          className="btn register"
          onClick={getRegistered}
        >
          Inscription
        </Button>
      </div>
    </div>
  );
};

export default Welcome;
